# Function to perform OCR using Google Generative AI Flash
def perform_ocr(image_data):
    try:
        response = model.generate_content([
            "OCR the medicine name and the compositions names from this image and only output those data in this formate : medicine name  compositions names",
            image_data,
        ])
        return response.text.strip()
    except Exception as e:
        print("OCR Error:", e)
        return "OCR failed"



# Function to search Google and extract the first 1mg link
def get_1mg_link(medicine_name):
    query = f"{medicine_name} site:1mg.com"
    search_results = list(search(query, num_results=5))
    for result in search_results:
        if "1mg.com" in result:
            return result  
    return search_results[0] if search_results else None  

